import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, View, Image, FlatList } from 'react-native';

import { Audio } from 'expo-av';

export default function App() {
  const [listmusic, setListmusic] = useState();
  const [sound, setSound] = useState("");
  const [inplay, setInplay] = useState();

  const getList = async () => {
    try {
      const response = await fetch('http://localhost:3333/music');
      const json = await response.json();
      setListmusic(json);
    }catch(err) {
      console.error(err.message);
    }
  }

  async function playSound() {
    if(inplay !== undefined) {
      inplay.sound.unloadAsync();
    }

    const sd = await Audio.Sound.createAsync({uri:sound});

    setInplay(sd);
  }

  useEffect(() => {
    getList();
  }, []);

  useEffect(() => {
    if(sound !== undefined) playSound();
  }, [sound])

  useEffect(() => {
    if(inplay !== undefined)  inplay.sound.playAsync();
  }, [inplay]);

  const handleMusic = (uri) => {
    setSound(uri);
  }

  const itemlist = ({item}) => (
    <View
      onClick={() => {handleMusic(item.uri)}}
    >
      <Image 
        style={styles.thumbnail}
        source={{uri:item.art}}
      ></Image>
    </View>
  )

  return (
    <View style={styles.container}>
      <Text>Adicionadas Recentemente</Text>
      <FlatList
        data={listmusic}
        horizontal={true}
        renderItem={itemlist}
        keyExtractor={item => item.uri}
      ></FlatList>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#454545'
  },
  thumbnail: {
    width: '30vw',
    height: '30vw',
    objectFit: 'cover',
    boxShadow: '0 0 10px rgba(0,0,0,.5)',
    marginLeft: '2vw'
  }
});
